import React, { useState, useEffect } from 'react';
import {
  Container,
  Card,
  CardContent,
  Typography,
  Box,
  LinearProgress,
  Grid,
  Pagination,
  CircularProgress,
  Alert,
  Chip,
  Button,
} from '@mui/material';
import {
  Book,
  Schedule,
  TrendingUp,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { historyAPI } from '../../services/api';

const ReadingHistory = () => {
  const [history, setHistory] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const navigate = useNavigate();

  useEffect(() => {
    fetchHistory();
    fetchStats();
  }, [page]);

  const fetchHistory = async () => {
    try {
      setLoading(true);
      const response = await historyAPI.getHistory({ page, limit: 10 });
      setHistory(response.data.history);
      setTotalPages(response.data.pages);
    } catch (err) {
      setError('Failed to load reading history');
      console.error('History fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await historyAPI.getStats();
      setStats(response.data.stats);
    } catch (err) {
      console.error('Stats fetch error:', err);
    }
  };

  const handlePageChange = (event, newPage) => {
    setPage(newPage);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const getProgressColor = (percentage) => {
    if (percentage >= 100) return 'success';
    if (percentage >= 75) return 'info';
    if (percentage >= 50) return 'warning';
    return 'error';
  };

  const getStatusChip = (percentage) => {
    if (percentage >= 100) {
      return <Chip label="Completed" color="success" size="small" />;
    } else if (percentage >= 75) {
      return <Chip label="Almost Done" color="info" size="small" />;
    } else if (percentage >= 25) {
      return <Chip label="In Progress" color="warning" size="small" />;
    } else {
      return <Chip label="Just Started" color="default" size="small" />;
    }
  };

  if (loading && page === 1) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
          <CircularProgress />
        </Box>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" gutterBottom>
        Reading History
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      {/* Statistics Cards */}
      {stats && (
        <Grid container spacing={3} sx={{ mb: 4 }}>
          <Grid item xs={12} sm={6} md={3}>
            <Card>
              <CardContent>
                <Box display="flex" alignItems="center" justifyContent="space-between">
                  <Box>
                    <Typography color="textSecondary" gutterBottom variant="body2">
                      Books Started
                    </Typography>
                    <Typography variant="h4" component="div">
                      {stats.total_books}
                    </Typography>
                  </Box>
                  <Book color="primary" fontSize="large" />
                </Box>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <Card>
              <CardContent>
                <Box display="flex" alignItems="center" justifyContent="space-between">
                  <Box>
                    <Typography color="textSecondary" gutterBottom variant="body2">
                      Books Completed
                    </Typography>
                    <Typography variant="h4" component="div">
                      {stats.completed_books}
                    </Typography>
                  </Box>
                  <TrendingUp color="success" fontSize="large" />
                </Box>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <Card>
              <CardContent>
                <Box display="flex" alignItems="center" justifyContent="space-between">
                  <Box>
                    <Typography color="textSecondary" gutterBottom variant="body2">
                      Pages Read
                    </Typography>
                    <Typography variant="h4" component="div">
                      {stats.total_pages_read}
                    </Typography>
                  </Box>
                  <Schedule color="info" fontSize="large" />
                </Box>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <Card>
              <CardContent>
                <Box display="flex" alignItems="center" justifyContent="space-between">
                  <Box>
                    <Typography color="textSecondary" gutterBottom variant="body2">
                      Average Progress
                    </Typography>
                    <Typography variant="h4" component="div">
                      {Math.round(stats.avg_progress)}%
                    </Typography>
                  </Box>
                  <TrendingUp color="warning" fontSize="large" />
                </Box>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      )}

      {/* Reading History List */}
      {history.length === 0 ? (
        <Card>
          <CardContent>
            <Typography variant="h6" textAlign="center" color="textSecondary">
              No reading history found
            </Typography>
            <Typography variant="body2" textAlign="center" color="textSecondary" sx={{ mt: 1 }}>
              Start reading some books to see your progress here!
            </Typography>
            <Box textAlign="center" mt={2}>
              <Button
                variant="contained"
                onClick={() => navigate('/books')}
              >
                Browse Books
              </Button>
            </Box>
          </CardContent>
        </Card>
      ) : (
        <>
          <Grid container spacing={3}>
            {history.map((item) => (
              <Grid item xs={12} key={item._id}>
                <Card
                  sx={{
                    cursor: 'pointer',
                    '&:hover': {
                      boxShadow: 4,
                      transform: 'translateY(-1px)',
                      transition: 'all 0.3s ease-in-out'
                    }
                  }}
                  onClick={() => navigate(`/books/${item.book_id}`)}
                >
                  <CardContent>
                    <Grid container spacing={2} alignItems="center">
                      <Grid item xs={12} md={6}>
                        <Typography variant="h6" gutterBottom>
                          {item.book.title}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" gutterBottom>
                          by {item.book.author}
                        </Typography>
                        <Box display="flex" alignItems="center" gap={1} mt={1}>
                          {getStatusChip(item.progress_percentage)}
                          {item.book.genre && (
                            <Chip label={item.book.genre} variant="outlined" size="small" />
                          )}
                        </Box>
                      </Grid>

                      <Grid item xs={12} md={4}>
                        <Box mb={1}>
                          <Typography variant="body2" color="textSecondary">
                            Progress: {item.current_page} / {item.total_pages} pages
                          </Typography>
                          <LinearProgress
                            variant="determinate"
                            value={item.progress_percentage}
                            color={getProgressColor(item.progress_percentage)}
                            sx={{ mt: 1, height: 8, borderRadius: 4 }}
                          />
                          <Typography variant="caption" color="textSecondary">
                            {Math.round(item.progress_percentage)}% complete
                          </Typography>
                        </Box>
                      </Grid>

                      <Grid item xs={12} md={2}>
                        <Typography variant="body2" color="textSecondary">
                          Last read:
                        </Typography>
                        <Typography variant="caption" color="textSecondary">
                          {formatDate(item.last_read)}
                        </Typography>
                        {item.started_reading && (
                          <>
                            <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
                              Started:
                            </Typography>
                            <Typography variant="caption" color="textSecondary">
                              {formatDate(item.started_reading)}
                            </Typography>
                          </>
                        )}
                      </Grid>
                    </Grid>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>

          {/* Pagination */}
          {totalPages > 1 && (
            <Box display="flex" justifyContent="center" mt={4}>
              <Pagination
                count={totalPages}
                page={page}
                onChange={handlePageChange}
                color="primary"
                size="large"
              />
            </Box>
          )}
        </>
      )}
    </Container>
  );
};

export default ReadingHistory;
