import React, { useState, Fragment } from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  IconButton,
  Menu,
  MenuItem,
  Avatar,
  Box,
  useTheme,
  useMediaQuery,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider,
} from '@mui/material';
import {
  Menu as MenuIcon,
  AccountCircle,
  Book,
  Home as HomeIcon,
  Info as AboutIcon,
  FeaturedPlayList as FeaturesIcon,
  History,
  Dashboard as DashboardIcon,
  Add as AddIcon,
  Logout,
  Settings,
} from '@mui/icons-material';
import { useNavigate, useLocation, Link as RouterLink } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';

const Navbar = () => {
  const [anchorEl, setAnchorEl] = useState(null);
  const [mobileOpen, setMobileOpen] = useState(false);
  const { user, logout, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  const handleProfileMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
    handleMenuClose();
  };

  const publicMenuItems = [
    { text: 'Home', icon: <HomeIcon />, path: '/' },
    { text: 'Features', icon: <FeaturesIcon />, path: '/features' },
    { text: 'About', icon: <AboutIcon />, path: '/about' },
  ];

  const protectedMenuItems = [
    { text: 'Dashboard', icon: <DashboardIcon />, path: '/dashboard' },
    { text: 'Books', icon: <Book />, path: '/books' },
    { text: 'Add Book', icon: <AddIcon />, path: '/add-book' },
    { text: 'Reading History', icon: <History />, path: '/history' },
  ];

  const drawer = (
    <Box sx={{ width: 250 }}>
      <Box sx={{ p: 2, borderBottom: 1, borderColor: 'divider' }}>
        <Typography variant="h6" component="div">
          E-Reader Platform
        </Typography>
      </Box>
      <List>
        {menuItems.map((item) => (
          <ListItem
            button
            key={item.text}
            onClick={() => {
              navigate(item.path);
              setMobileOpen(false);
            }}
            selected={location.pathname === item.path}
          >
            <ListItemIcon>{item.icon}</ListItemIcon>
            <ListItemText primary={item.text} />
          </ListItem>
        ))}
      </List>
    </Box>
  );

  const renderAuthButtons = () => (
    <Box sx={{ display: 'flex', gap: 1 }}>
      <Button 
        color="inherit" 
        component={RouterLink} 
        to="/login"
        sx={{
          '&:hover': {
            backgroundColor: 'rgba(255, 255, 255, 0.1)',
          },
        }}
      >
        Login
      </Button>
      <Button 
        variant="outlined" 
        color="inherit" 
        component={RouterLink} 
        to="/register"
        sx={{
          borderColor: 'inherit',
          '&:hover': {
            backgroundColor: 'rgba(255, 255, 255, 0.1)',
          },
        }}
      >
        Register
      </Button>
    </Box>
  );

  const renderNavItems = (items) => (
    <Box sx={{ display: 'flex', gap: 1 }}>
      {items.map((item) => (
        <Button
          key={item.path}
          color="inherit"
          component={RouterLink}
          to={item.path}
          startIcon={!isMobile ? item.icon : null}
          sx={{
            textTransform: 'none',
            '&:hover': {
              backgroundColor: 'rgba(255, 255, 255, 0.1)',
            },
            ...(location.pathname === item.path ? {
              backgroundColor: 'rgba(255, 255, 255, 0.1)',
              fontWeight: 'bold',
            } : {}),
          }}
        >
          {item.text}
        </Button>
      ))}
    </Box>
  );

  const appTitle = (
    <Typography 
      variant="h6" 
      component={RouterLink} 
      to="/"
      sx={{
        flexGrow: { xs: 1, md: 0 },
        mr: { md: 3 },
        fontWeight: 'bold',
        textDecoration: 'none',
        color: 'inherit',
        '&:hover': {
          opacity: 0.9,
        },
      }}
    >
      E-Reader Platform
    </Typography>
  );

  return (
    <>
      <AppBar position="static" elevation={1}>
        <Toolbar>
          {isMobile && (
            <IconButton
              color="inherit"
              aria-label="open drawer"
              edge="start"
              onClick={handleDrawerToggle}
              sx={{ mr: 2 }}
            >
              <MenuIcon />
            </IconButton>
          )}

          {appTitle}

          {!isMobile && (
            <Box sx={{ flexGrow: 1, display: 'flex', justifyContent: 'center' }}>
              {isAuthenticated 
                ? renderNavItems(protectedMenuItems)
                : renderNavItems(publicMenuItems)
              }
            </Box>
          )}

          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            {!isAuthenticated ? (
              renderAuthButtons()
            ) : (
              <IconButton
                size="large"
                aria-label="account of current user"
                aria-controls="menu-appbar"
                aria-haspopup="true"
                onClick={handleProfileMenuOpen}
                color="inherit"
                sx={{ ml: 1 }}
              >
                {user?.profile_picture ? (
                  <Avatar src={user.profile_picture} sx={{ width: 32, height: 32 }} />
                ) : (
                  <AccountCircle />
                )}
              </IconButton>
            )}
          </Box>
        </Toolbar>
      </AppBar>

      <Menu
        id="menu-appbar"
        anchorEl={anchorEl}
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'right',
        }}
        keepMounted
        transformOrigin={{
          vertical: 'top',
          horizontal: 'right',
        }}
        open={Boolean(anchorEl)}
        onClose={handleMenuClose}
        PaperProps={{
          elevation: 3,
          sx: {
            width: 200,
            overflow: 'visible',
            mt: 1.5,
            '&:before': {
              content: '""',
              display: 'block',
              position: 'absolute',
              top: 0,
              right: 14,
              width: 10,
              height: 10,
              bgcolor: 'background.paper',
              transform: 'translateY(-50%) rotate(45deg)',
              zIndex: 0,
            },
          },
        }}
      >
        <Box sx={{ px: 2, py: 1 }}>
          <Typography variant="subtitle1" fontWeight="medium">
            {user?.name || 'User'}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {user?.email || ''}
          </Typography>
        </Box>
        <Divider sx={{ my: 1 }} />
        <MenuItem 
          onClick={() => { 
            navigate('/profile'); 
            handleMenuClose(); 
          }}
          sx={{ py: 1.5 }}
        >
          <ListItemIcon>
            <Settings fontSize="small" />
          </ListItemIcon>
          <ListItemText>Profile & Settings</ListItemText>
        </MenuItem>
        <Divider sx={{ my: 1 }} />
        <MenuItem 
          onClick={handleLogout}
          sx={{ py: 1.5 }}
        >
          <ListItemIcon>
            <Logout fontSize="small" />
          </ListItemIcon>
          <ListItemText>Logout</ListItemText>
        </MenuItem>
      </Menu>

      {isMobile && (
        <Drawer
          variant="temporary"
          open={mobileOpen}
          onClose={handleDrawerToggle}
          ModalProps={{
            keepMounted: true,
          }}
          sx={{
            '& .MuiDrawer-paper': { 
              boxSizing: 'border-box', 
              width: 280,
              pt: 2,
            },
          }}
        >
          <Box sx={{ px: 2, mb: 2 }}>
            {appTitle}
          </Box>
          <Divider />
          <List>
            {isAuthenticated ? (
              <>
                {protectedMenuItems.map((item) => (
                  <ListItem
                    button
                    key={item.path}
                    component={RouterLink}
                    to={item.path}
                    onClick={() => setMobileOpen(false)}
                    selected={location.pathname === item.path}
                    sx={{
                      borderRadius: 1,
                      mx: 1,
                      mb: 0.5,
                      '&.Mui-selected': {
                        backgroundColor: 'primary.main',
                        color: 'primary.contrastText',
                        '&:hover': {
                          backgroundColor: 'primary.dark',
                        },
                        '& .MuiListItemIcon-root': {
                          color: 'primary.contrastText',
                        },
                      },
                    }}
                  >
                    <ListItemIcon>{item.icon}</ListItemIcon>
                    <ListItemText primary={item.text} />
                  </ListItem>
                ))}
                <Divider sx={{ my: 2 }} />
                <ListItem
                  button
                  component={RouterLink}
                  to="/profile"
                  onClick={() => setMobileOpen(false)}
                  sx={{
                    borderRadius: 1,
                    mx: 1,
                    mb: 0.5,
                  }}
                >
                  <ListItemIcon><Settings /></ListItemIcon>
                  <ListItemText primary="Profile & Settings" />
                </ListItem>
                <ListItem
                  button
                  onClick={() => {
                    handleLogout();
                    setMobileOpen(false);
                  }}
                  sx={{
                    borderRadius: 1,
                    mx: 1,
                    mb: 0.5,
                  }}
                >
                  <ListItemIcon><Logout /></ListItemIcon>
                  <ListItemText primary="Logout" />
                </ListItem>
              </>
            ) : (
              <>
                {publicMenuItems.map((item) => (
                  <ListItem
                    button
                    key={item.path}
                    component={RouterLink}
                    to={item.path}
                    onClick={() => setMobileOpen(false)}
                    selected={location.pathname === item.path}
                    sx={{
                      borderRadius: 1,
                      mx: 1,
                      mb: 0.5,
                      '&.Mui-selected': {
                        backgroundColor: 'primary.main',
                        color: 'primary.contrastText',
                        '&:hover': {
                          backgroundColor: 'primary.dark',
                        },
                        '& .MuiListItemIcon-root': {
                          color: 'primary.contrastText',
                        },
                      },
                    }}
                  >
                    <ListItemIcon>{item.icon}</ListItemIcon>
                    <ListItemText primary={item.text} />
                  </ListItem>
                ))}
                <Divider sx={{ my: 2 }} />
                <Box sx={{ px: 2, display: 'flex', gap: 1, flexDirection: 'column' }}>
                  <Button
                    fullWidth
                    variant="contained"
                    color="primary"
                    component={RouterLink}
                    to="/login"
                    onClick={() => setMobileOpen(false)}
                    sx={{ mt: 1 }}
                  >
                    Login
                  </Button>
                  <Button
                    fullWidth
                    variant="outlined"
                    color="inherit"
                    component={RouterLink}
                    to="/register"
                    onClick={() => setMobileOpen(false)}
                  >
                    Register
                  </Button>
                </Box>
              </>
            )}
          </List>
        </Drawer>
      )}
    </>
  );
};

export default Navbar;
