import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Button,
  IconButton,
  LinearProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Slider,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Alert,
  CircularProgress,
} from '@mui/material';
import {
  ArrowBack,
  ArrowForward,
  Settings,
  Bookmark,
  Close,
} from '@mui/icons-material';
import { useParams, useNavigate } from 'react-router-dom';
import { booksAPI } from '../../services/api';
import { useAuth } from '../../contexts/AuthContext';

const BookReader = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [book, setBook] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [pages, setPages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [settingsOpen, setSettingsOpen] = useState(false);
  const [fontSize, setFontSize] = useState(16);
  const [theme, setTheme] = useState('light');
  const [progress, setProgress] = useState(null);

  useEffect(() => {
    fetchBook();
    fetchProgress();
  }, [id]);

  useEffect(() => {
    if (book && book.content) {
      // Split content into pages (simple implementation)
      const wordsPerPage = 300;
      const words = book.content.split(' ');
      const pageCount = Math.ceil(words.length / wordsPerPage);

      const bookPages = [];
      for (let i = 0; i < pageCount; i++) {
        const startIndex = i * wordsPerPage;
        const endIndex = Math.min((i + 1) * wordsPerPage, words.length);
        const pageContent = words.slice(startIndex, endIndex).join(' ');
        bookPages.push(pageContent);
      }

      setPages(bookPages);
      setTotalPages(pageCount);

      // Set current page from progress or start from beginning
      if (progress && progress.current_page) {
        setCurrentPage(progress.current_page);
      }
    }
  }, [book, progress]);

  useEffect(() => {
    // Update reading progress when page changes
    if (book && currentPage > 0) {
      updateProgress();
    }
  }, [currentPage, book]);

  const fetchBook = async () => {
    try {
      const response = await booksAPI.getBook(id);
      setBook(response.data.book);
    } catch (err) {
      setError('Failed to load book');
      console.error('Book fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  const fetchProgress = async () => {
    try {
      const response = await booksAPI.getProgress(id);
      setProgress(response.data.progress);
    } catch (err) {
      // Progress might not exist yet, that's okay
      console.log('No progress found for this book');
    }
  };

  const updateProgress = async () => {
    if (!user) return; // Don't track progress for non-authenticated users

    try {
      const response = await booksAPI.updateProgress(id, {
        current_page: currentPage,
        total_pages: totalPages,
      });

      // Update local progress state with the latest from server
      if (response.data && response.data.progress) {
        setProgress(response.data.progress);
      }

      // Notify user when they complete the book
      if (currentPage === totalPages && !progress?.completed) {
        // Only show the completion message if we haven't already
        if (!progress || progress.current_page < totalPages) {
          // You can add a toast notification here if you have one
          console.log('Book completed!');
        }
      }
    } catch (err) {
      console.error('Failed to update progress:', err);
      // Consider adding a retry mechanism here
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(prevPage => {
        const newPage = prevPage + 1;
        // If this is the first page, we're starting the book
        if (prevPage === 1 && !progress) {
          // You can add analytics or additional logic for book start
          console.log('Started reading book');
        }
        return newPage;
      });
    }
  };

  const handlePageJump = (event, newPage) => {
    setCurrentPage(Math.min(Math.max(1, newPage), totalPages));
  };

  const progressPercentage = totalPages > 0 ? Math.min(100, Math.max(0, (currentPage / totalPages) * 100)) : 0;

  // Calculate time spent reading (simple implementation)
  const calculateReadingTime = () => {
    if (!progress?.started_reading) return 'Just started';

    const started = new Date(progress.started_reading);
    const now = new Date();
    const diffMs = now - started;
    const diffMins = Math.round(diffMs / 60000);

    if (diffMins < 1) return 'Just started';
    if (diffMins < 60) return `${diffMins} min`;

    const hours = Math.floor(diffMins / 60);
    const mins = diffMins % 60;
    return `${hours}h ${mins}m`;
  };

  if (loading) {
    return (
      <Container maxWidth="md" sx={{ mt: 4, mb: 4 }}>
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
          <CircularProgress />
        </Box>
      </Container>
    );
  }

  if (error || !book) {
    return (
      <Container maxWidth="md" sx={{ mt: 4, mb: 4 }}>
        <Alert severity="error">
          {error || 'Book not found'}
        </Alert>
        <Box mt={2}>
          <Button variant="contained" onClick={() => navigate('/books')}>
            Back to Books
          </Button>
        </Box>
      </Container>
    );
  }

  return (
    <Box sx={{ minHeight: '100vh', backgroundColor: theme === 'dark' ? '#1a1a1a' : '#ffffff' }}>
      {/* Header */}
      <Paper 
        elevation={1} 
        sx={{ 
          p: 2, 
          position: 'sticky', 
          top: 0, 
          zIndex: 1000,
          backgroundColor: theme === 'dark' ? '#2a2a2a' : '#ffffff'
        }}
      >
        <Box display="flex" alignItems="center" justifyContent="space-between">
          <Box display="flex" alignItems="center">
            <IconButton onClick={() => navigate('/books')} sx={{ mr: 2 }}>
              <Close />
            </IconButton>
            <Box>
              <Typography variant="h6" noWrap sx={{ maxWidth: 300 }}>
                {book.title}
              </Typography>
              <Typography variant="body2" color="textSecondary">
                by {book.author}
              </Typography>
            </Box>
          </Box>

          <Box display="flex" alignItems="center" gap={2}>
            <Box sx={{ textAlign: 'right' }}>
              <Typography variant="body2">
                Page {currentPage} of {totalPages}
              </Typography>
              <Typography variant="caption" color="textSecondary">
                {progressPercentage.toFixed(1)}% • {calculateReadingTime()}
              </Typography>
            </Box>
            <IconButton onClick={() => setSettingsOpen(true)}>
              <Settings />
            </IconButton>
          </Box>
        </Box>

        <Box mt={2}>
          <LinearProgress 
            variant="determinate" 
            value={progressPercentage} 
            sx={{ height: 6, borderRadius: 3 }}
          />
          <Typography variant="caption" color="textSecondary">
            {Math.round(progressPercentage)}% complete
          </Typography>
        </Box>
      </Paper>

      {/* Reading Content */}
      <Container maxWidth="md" sx={{ py: 4 }}>
        <Paper 
          elevation={0} 
          sx={{ 
            p: 4, 
            minHeight: '70vh',
            backgroundColor: theme === 'dark' ? '#2a2a2a' : '#ffffff',
            color: theme === 'dark' ? '#ffffff' : '#000000'
          }}
        >
          <Typography 
            variant="body1" 
            sx={{ 
              fontSize: `${fontSize}px`,
              lineHeight: 1.8,
              textAlign: 'justify',
              fontFamily: 'Georgia, serif'
            }}
          >
            {pages[currentPage - 1] || 'Content not available'}
          </Typography>
        </Paper>

        {/* Navigation */}
        <Box display="flex" justifyContent="space-between" alignItems="center" mt={3}>
          <Button
            variant="outlined"
            startIcon={<ArrowBack />}
            onClick={handlePreviousPage}
            disabled={currentPage <= 1}
          >
            Previous
          </Button>

          <Box display="flex" alignItems="center" gap={2}>
            <Typography variant="body2">Go to page:</Typography>
            <Box sx={{ width: 200 }}>
              <Slider
                value={currentPage}
                onChange={handlePageJump}
                min={1}
                max={totalPages}
                valueLabelDisplay="auto"
                marks
                step={1}
              />
            </Box>
          </Box>

          <Button
            variant="outlined"
            endIcon={<ArrowForward />}
            onClick={handleNextPage}
            disabled={currentPage >= totalPages}
          >
            Next
          </Button>
        </Box>
      </Container>

      {/* Settings Dialog */}
      <Dialog open={settingsOpen} onClose={() => setSettingsOpen(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Reading Settings</DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 2 }}>
            <Typography gutterBottom>Font Size</Typography>
            <Slider
              value={fontSize}
              onChange={(e, newValue) => setFontSize(newValue)}
              min={12}
              max={24}
              valueLabelDisplay="auto"
              marks={[
                { value: 12, label: 'Small' },
                { value: 16, label: 'Medium' },
                { value: 20, label: 'Large' },
                { value: 24, label: 'Extra Large' },
              ]}
            />

            <Box mt={3}>
              <FormControl fullWidth>
                <InputLabel>Theme</InputLabel>
                <Select
                  value={theme}
                  label="Theme"
                  onChange={(e) => setTheme(e.target.value)}
                >
                  <MenuItem value="light">Light</MenuItem>
                  <MenuItem value="dark">Dark</MenuItem>
                </Select>
              </FormControl>
            </Box>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setSettingsOpen(false)}>Close</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default BookReader;
