import React from 'react';
import { 
  Box, 
  Container, 
  Typography, 
  Paper, 
  Avatar, 
  Button,
  Card,
  CardContent,
  Grid,
  useTheme,
  useMediaQuery,
  styled
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

// Styled components for enhanced styling
const StyledHeroSection = styled(Box)(({ theme }) => ({
  background: 'linear-gradient(135deg, #1a237e 0%, #303f9f 50%, #3949ab 100%)',
  color: '#ffffff',
  padding: theme.spacing(12, 0),
  marginBottom: theme.spacing(8),
  textAlign: 'center',
  position: 'relative',
  overflow: 'hidden',
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: 'url("data:image/svg+xml,%3Csvg width="60" height="60" viewBox="0 0 60 60" xmlns="http://www.w3.org/2000/svg"%3E%3Cg fill="none" fill-rule="evenodd"%3E%3Cg fill="%23ffffff" fill-opacity="0.05"%3E%3Ccircle cx="30" cy="30" r="4"/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")',
    opacity: 0.1
  }
}));

const StyledCard = styled(Card)(({ theme }) => ({
  height: '100%',
  transition: 'transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out',
  border: `1px solid ${theme.palette.grey[100]}`,
  '&:hover': {
    transform: 'translateY(-8px)',
    boxShadow: '0 12px 24px rgba(26, 35, 126, 0.15)'
  }
}));

const StyledTeamCard = styled(Card)(({ theme }) => ({
  height: '100%',
  transition: 'all 0.3s ease-in-out',
  border: `2px solid transparent`,
  background: '#ffffff',
  '&:hover': {
    transform: 'translateY(-5px)',
    borderColor: '#1a237e',
    boxShadow: '0 8px 32px rgba(26, 35, 126, 0.2)'
  }
}));

const StyledButton = styled(Button)(({ theme }) => ({
  background: 'linear-gradient(45deg, #1a237e 30%, #303f9f 90%)',
  borderRadius: '25px',
  padding: '12px 36px',
  fontSize: '1.1rem',
  fontWeight: 600,
  textTransform: 'none',
  boxShadow: '0 4px 16px rgba(26, 35, 126, 0.3)',
  '&:hover': {
    background: 'linear-gradient(45deg, #0d1654 30%, #1a237e 90%)',
    transform: 'translateY(-2px)',
    boxShadow: '0 8px 24px rgba(26, 35, 126, 0.4)'
  }
}));

const AboutPage = () => {
  const theme = useTheme();
  const navigate = useNavigate();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const isTablet = useMediaQuery(theme.breakpoints.down('md'));

  const teamMembers = [
    {
      name: 'John Doe',
      role: 'Founder & CEO',
      bio: 'Passionate about reading and technology, John started this platform to make reading more accessible to everyone.',
      color: '#1a237e'
    },
    {
      name: 'Jane Smith',
      role: 'Lead Developer',
      bio: 'Full-stack developer with a love for creating seamless reading experiences across all devices.',
      color: '#303f9f'
    },
    {
      name: 'Alex Johnson',
      role: 'UI/UX Designer',
      bio: 'Focused on creating intuitive and beautiful interfaces that make reading a joy.',
      color: '#3949ab'
    }
  ];

  return (
    <Box sx={{ 
      minHeight: '100vh',
      bgcolor: '#fafafa'
    }}>
      {/* Hero Section */}
      <StyledHeroSection>
        <Container maxWidth="lg">
          <Typography 
            variant={isMobile ? 'h3' : isTablet ? 'h2' : 'h1'} 
            component="h1"
            gutterBottom
            sx={{ 
              fontWeight: 700,
              mb: 3,
              fontSize: {
                xs: '2.5rem',
                sm: '3.5rem',
                md: '4rem',
                lg: '4.5rem'
              },
              lineHeight: 1.1
            }}
          >
            About Our E-Reader Platform
          </Typography>
          <Typography 
            variant={isMobile ? 'h6' : 'h5'} 
            sx={{ 
              maxWidth: 700, 
              mx: 'auto', 
              opacity: 0.95,
              fontSize: {
                xs: '1.1rem',
                sm: '1.3rem',
                md: '1.5rem'
              },
              lineHeight: 1.4,
              fontWeight: 300
            }}
          >
            Empowering readers with a modern, accessible, and feature-rich digital reading experience.
          </Typography>
        </Container>
      </StyledHeroSection>

      <Container maxWidth="lg" sx={{ pb: 8 }}>
        {/* Our Story */}
        <Box sx={{ mb: { xs: 6, md: 8 }, textAlign: 'center' }}>
          <Typography 
            variant={isMobile ? 'h4' : 'h3'} 
            component="h2" 
            gutterBottom
            sx={{ 
              color: '#1a237e',
              fontWeight: 700,
              mb: 4
            }}
          >
            Our Story
          </Typography>
          <Typography 
            variant="h6" 
            color="text.secondary" 
            sx={{ 
              maxWidth: 900, 
              mx: 'auto',
              lineHeight: 1.7,
              fontSize: {
                xs: '1rem',
