import React from 'react';
import { Box, Button, Container, Typography, useTheme, useMediaQuery } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const LandingPage = () => {
  const theme = useTheme();
  const navigate = useNavigate();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  return (
    <Box sx={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
      {/* Hero Section */}
      <Box 
        sx={{
          py: 12,
          px: 2,
          textAlign: 'center',
          backgroundColor: theme.palette.primary.main,
          color: theme.palette.primary.contrastText,
          flex: 1,
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
        }}
      >
        <Container maxWidth="md">
          <Typography 
            variant={isMobile ? 'h3' : 'h2'} 
            component="h1" 
            gutterBottom
            sx={{ fontWeight: 'bold', mb: 3 }}
          >
            Your Personal Digital Library
          </Typography>
          <Typography 
            variant={isMobile ? 'h6' : 'h5'} 
            component="h2" 
            gutterBottom
            sx={{ mb: 4, opacity: 0.9 }}
          >
            Read, discover, and track your reading journey all in one place.
          </Typography>
          <Box sx={{ mt: 4, display: 'flex', gap: 2, justifyContent: 'center', flexWrap: 'wrap' }}>
            <Button 
              variant="contained" 
              color="secondary" 
              size="large"
              onClick={() => navigate('/register')}
              sx={{ px: 4, py: 1.5 }}
            >
              Get Started
            </Button>
            <Button 
              variant="outlined" 
              color="inherit"
              size="large"
              onClick={() => navigate('/features')}
              sx={{ px: 4, py: 1.5 }}
            >
              Learn More
            </Button>
          </Box>
        </Container>
      </Box>

      {/* Features Preview */}
      <Box sx={{ py: 8, bgcolor: 'background.paper' }}>
        <Container maxWidth="lg">
          <Typography variant="h4" component="h2" align="center" gutterBottom>
            Why Choose Our E-Reader?
          </Typography>
          <Box sx={{ mt: 6, display: 'grid', gridTemplateColumns: { xs: '1fr', md: 'repeat(3, 1fr)' }, gap: 4 }}>
            {[
              {
                title: 'Personalized Library',
                description: 'Build your own digital book collection and access it anywhere, anytime.'
              },
              {
                title: 'Reading Progress',
                description: 'Track your reading progress and pick up right where you left off.'
              },
              {
                title: 'Cross-Device Sync',
                description: 'Seamlessly switch between devices without losing your place.'
              }
            ].map((feature, index) => (
              <Box key={index} sx={{ textAlign: 'center', p: 3 }}>
                <Box 
                  sx={{
                    width: 80,
                    height: 80,
                    borderRadius: '50%',
                    bgcolor: 'primary.main',
                    display: 'inline-flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    color: 'primary.contrastText',
                    mb: 2,
                    fontSize: '2rem'
                  }}
                >
                  {index + 1}
                </Box>
                <Typography variant="h6" gutterBottom>{feature.title}</Typography>
                <Typography color="text.secondary">{feature.description}</Typography>
              </Box>
            ))}
          </Box>
        </Container>
      </Box>
    </Box>
  );
};

export default LandingPage;
