import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Card,
  CardContent,
  Typography,
  Box,
  TextField,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Pagination,
  CircularProgress,
  Alert,
  Chip,
} from '@mui/material';
import {
  Search,
  Add,
  FilterList,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { booksAPI } from '../../services/api';

const BooksList = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [authorFilter, setAuthorFilter] = useState('');
  const [sortBy, setSortBy] = useState('updated_at');
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [total, setTotal] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    fetchBooks();
  }, [page, sortBy]);

  useEffect(() => {
    const delayedSearch = setTimeout(() => {
      if (page === 1) {
        fetchBooks();
      } else {
        setPage(1);
      }
    }, 500);

    return () => clearTimeout(delayedSearch);
  }, [searchTerm, authorFilter]);

  const fetchBooks = async () => {
    try {
      setLoading(true);
      const params = {
        page,
        limit: 12,
        search: searchTerm,
        author: authorFilter,
        sort: sortBy,
      };

      const response = await booksAPI.getBooks(params);
      setBooks(response.data.books);
      setTotalPages(response.data.pages);
      setTotal(response.data.total);
    } catch (err) {
      setError('Failed to load books');
      console.error('Books fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handlePageChange = (event, newPage) => {
    setPage(newPage);
  };

  const clearFilters = () => {
    setSearchTerm('');
    setAuthorFilter('');
    setSortBy('updated_at');
    setPage(1);
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h4" gutterBottom>
          Books Library
        </Typography>
        <Button
          variant="contained"
          startIcon={<Add />}
          onClick={() => navigate('/add-book')}
        >
          Add Book
        </Button>
      </Box>

      {/* Search and Filter Controls */}
      <Card sx={{ mb: 3 }}>
        <CardContent>
          <Grid container spacing={2} alignItems="center">
            <Grid item xs={12} md={4}>
              <TextField
                fullWidth
                label="Search books..."
                variant="outlined"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                InputProps={{
                  startAdornment: <Search sx={{ mr: 1, color: 'action.active' }} />,
                }}
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <TextField
                fullWidth
                label="Filter by author"
                variant="outlined"
                value={authorFilter}
                onChange={(e) => setAuthorFilter(e.target.value)}
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <FormControl fullWidth>
                <InputLabel>Sort by</InputLabel>
                <Select
                  value={sortBy}
                  label="Sort by"
                  onChange={(e) => setSortBy(e.target.value)}
                >
                  <MenuItem value="updated_at">Recently Updated</MenuItem>
                  <MenuItem value="created_at">Recently Added</MenuItem>
                  <MenuItem value="title">Title (A-Z)</MenuItem>
                  <MenuItem value="author">Author (A-Z)</MenuItem>
                  <MenuItem value="publication_date">Publication Date</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} md={2}>
              <Button
                fullWidth
                variant="outlined"
                startIcon={<FilterList />}
                onClick={clearFilters}
              >
                Clear
              </Button>
            </Grid>
          </Grid>

          {(searchTerm || authorFilter) && (
            <Box mt={2}>
              <Typography variant="body2" color="textSecondary">
                Showing {total} results
                {searchTerm && (
                  <Chip
                    label={`Search: "${searchTerm}"`}
                    onDelete={() => setSearchTerm('')}
                    size="small"
                    sx={{ ml: 1 }}
                  />
                )}
                {authorFilter && (
                  <Chip
                    label={`Author: "${authorFilter}"`}
                    onDelete={() => setAuthorFilter('')}
                    size="small"
                    sx={{ ml: 1 }}
                  />
                )}
              </Typography>
            </Box>
          )}
        </CardContent>
      </Card>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      {/* Books Grid */}
      {loading ? (
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
          <CircularProgress />
        </Box>
      ) : books.length === 0 ? (
        <Card>
          <CardContent>
            <Typography variant="h6" textAlign="center" color="textSecondary">
              {searchTerm || authorFilter ? 'No books found matching your criteria' : 'No books available'}
            </Typography>
            <Box textAlign="center" mt={2}>
              <Button
                variant="contained"
                startIcon={<Add />}
                onClick={() => navigate('/add-book')}
              >
                Add Your First Book
              </Button>
            </Box>
          </CardContent>
        </Card>
      ) : (
        <>
          <Grid container spacing={3}>
            {books.map((book) => (
              <Grid item xs={12} sm={6} md={4} lg={3} key={book._id}>
                <Card
                  sx={{
                    height: '100%',
                    display: 'flex',
                    flexDirection: 'column',
                    cursor: 'pointer',
                    '&:hover': {
                      boxShadow: 6,
                      transform: 'translateY(-2px)',
                      transition: 'all 0.3s ease-in-out'
                    }
                  }}
                  onClick={() => navigate(`/books/${book._id}`)}
                >
                  {book.cover_image && (
                    <Box
                      component="img"
                      src={book.cover_image}
                      alt={book.title}
                      sx={{
                        height: 200,
                        objectFit: 'cover',
                      }}
                    />
                  )}
                  <CardContent sx={{ flexGrow: 1 }}>
                    <Typography variant="h6" component="div" gutterBottom noWrap>
                      {book.title}
                    </Typography>
                    <Typography variant="body2" color="textSecondary" gutterBottom>
                      by {book.author}
                    </Typography>
                    <Typography variant="body2" color="textSecondary" sx={{ mb: 2 }}>
                      {book.description?.substring(0, 100)}
                      {book.description?.length > 100 ? '...' : ''}
                    </Typography>
                    <Box display="flex" justifyContent="space-between" alignItems="center">
                      {book.genre && (
                        <Chip label={book.genre} size="small" variant="outlined" />
                      )}
                      <Typography variant="caption" color="textSecondary">
                        {book.total_pages} pages
                      </Typography>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>

          {/* Pagination */}
          {totalPages > 1 && (
            <Box display="flex" justifyContent="center" mt={4}>
              <Pagination
                count={totalPages}
                page={page}
                onChange={handlePageChange}
                color="primary"
                size="large"
              />
            </Box>
          )}
        </>
      )}
    </Container>
  );
};

export default BooksList;
